package q536;

public class Main {
	public static void main(String[] args) {
		// TODO Auto-generated constructor stub
		int i=1;
		
		while (i<=15) {
			System.out.print(i+" ");
			i+=1;
		}
		
		
		System.out.println();
		i=1;
		do {
			System.out.print(i+" ");
			i+=1;
		}while (i<=15);
		
		System.out.println();
		for(int n=1;n<=15;n++) {
			System.out.print(n+" ");
		}
	}
}
